//Original box filter
//========================================
void BoxFilter(int filterSize,
               const float* input, int width, int height,
               float* output){
    
    for (int x = 0; x < width; ++x){
        for (int y = 0; y < height; ++y){
            
            float sum = 0.f;
            for (int ix = -filterSize; ix <= filterSize; ++ix) {
                for (int iy = -filterSize; iy <= filterSize; ++iy) {
                    int sampleX = MIN(MAX(0, x + ix + filterSize), width - 1);
                    int sampleY = MIN(MAX(0, y + iy + filterSize), height - 1);
                    sum += input[sampleY * width + sampleX];
                }
            }
            output[y * width + x] = sum / (filterSize * filterSize);
        }
    }
}

//Loop unrolling version of box filter
//========================================

float LoopUnrolledBoxFilter3By3( int x, int y, int width, int height, const float* input ){
    
    const int filterSize = 1;
    float sum = 0.0f;
    int addr;
    int sampleX;
    int sampleY;

    //(-1, -1)
    sampleX = MIN(MAX(0, x -1 + filterSize), width-1);
    sampleY = MIN(MAX(0, y -1 + filterSize), height-1);
    addr = sampleY * width + sampleX;
    sum +=  input[addr];

    //(0, -1)
    sampleX = MIN(MAX(0, x +0 + filterSize), width-1);
    sampleY = MIN(MAX(0, y -1 + filterSize), height-1);
    addr = sampleY * width + sampleX;
    sum += input[addr];

    //(1, -1)
    sampleX = MIN(MAX(0, x +1 + filterSize), width-1);
    sampleY = MIN(MAX(0, y -1 + filterSize), height-1);
    addr = sampleY * width + sampleX;
    sum += input[addr];
    
    
    //(-1, 0)
    sampleX = MIN(MAX(0, x -1 + filterSize), width-1);
    sampleY = MIN(MAX(0, y +0 + filterSize), height-1);
    addr = sampleY * width + sampleX;
    sum += input[addr];
    
    //( 0, 0)
    sampleX = MIN(MAX(0, x +0 + filterSize), width-1);
    sampleY = MIN(MAX(0, y +0 + filterSize), height-1);
    addr = sampleY * width + sampleX;
    sum += input[addr];
    
    //( 1, 0)
    sampleX = MIN(MAX(0, x +1 + filterSize), width-1);
    sampleY = MIN(MAX(0, y +0 + filterSize), height-1);
    addr = sampleY * width + sampleX;
    sum += input[addr];
    
    
    
    //(-1, 1)
    sampleX = MIN(MAX(0, x - 1 + filterSize), width-1);
    sampleY = MIN(MAX(0, y + 1 + filterSize), height-1);
    addr = sampleY * width + sampleX;
    sum += input[addr];
    
    //( 0, 1)
    sampleX = MIN(MAX(0, x + 0 + filterSize), width-1);
    sampleY = MIN(MAX(0, y + 1 + filterSize), height-1);
    addr = sampleY * width + sampleX;
    sum += input[addr];

    
    //( 1, 1)
    sampleX = MIN(MAX(0, x + 1 + filterSize), width-1);
    sampleY = MIN(MAX(0, y + 1 + filterSize), height-1);
    addr = sampleY * width + sampleX;
    sum += input[addr];


    return sum;
    
}



void BoxFilterLoopUnrolling(int filterSize,
                  const float* input, int width, int height,
                  float* output){
    
    for (int x = 0; x < width; ++x){
        for (int y = 0; y < height; ++y){
            
            float sum = 0.f;
            
            switch (filterSize) {
                case 1: //3x3
                    sum = LoopUnrolledBoxFilter3By3( x, y, width, height, input);
                    break;
                    
                default: //Naive implementation for different filter size.
                    for (int ix = -filterSize; ix <= filterSize; ++ix) {
                        for (int iy = -filterSize; iy <= filterSize; ++iy) {
                            int sampleX = MIN(MAX(0, x + ix + filterSize), width - 1);
                            int sampleY = MIN(MAX(0, y + iy + filterSize), height - 1);
                            sum += input[sampleY * width + sampleX];
                        }
                    }
                    break;
            }
            
            output[y * width + x] = sum / (filterSize * filterSize);
        }
    }
}


